package com.prathamesh.program;

public class SumOfNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//------------------------Sum Of n Numbers---------------//
		int sum=0;
		int n=10;
		for(int i = 0;i<=n;i++)
		{
			sum = sum + i;
			
		}
		System.out.println(sum);
//----------------------------------------------------------//
	}

}
